mapper.select:
select count(id) as cnt
from member
where id=#{id}

select count(email) as cnt
from member
where email=#{email}

SELECT *
FROM member
WHERE id=#{id} AND passwd=#{passwd}

mapper.update:
update member
   set mlevel='F1'
   where id=#{id} and passwd=#{passwd}

mapper.select:
SELECT id
FROM member
WHERE email=#{email} and mname=#{mname}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE email=#{email} and mname=#{mname}
